import React, { useEffect, useState, useRef } from 'react';
import { Link } from "react-router-dom";
import * as yup from 'yup';
import { faCheck, faTimes, faInfoCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const USER_REGEX = /^[A-z][A-z0-9-_]{3,23}$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;

function Register() {
  const usernameRef = useRef();
  const errRef = useRef();

  const [username, setUsername] = useState("");
  const [validUsername, setValidUsername] = useState(false);
  const [userFocus, setUserFocus] = useState(false);

  const [password, setPassword] = useState("");
  const [validPassword, setValidPassword] = useState(false);
  const [pswFocus, setPswFocus] = useState(false);

  const [matchPassword, setMatchPassword] = useState("");
  const [validMatch, setValidMatch] = useState(false);
  const [matchPswFocus, setMatchPswFocus] = useState(false);

  const [errMsg, setErrMsg] = useState([]);

  useEffect(() => {
    usernameRef.current.focus();
  }, []);

  useEffect(() => {
    schema.validate({ username }).then(() => {
      setValidUsername(true);
    }).catch(() => {
      setValidUsername(false);
    });
    console.log(validUsername);

    //setValidUsername(USER_REGEX.test(username));
  }, [username])

  useEffect(() => {
    schema.validate({ password }).then(() => {
      setValidPassword(true);
    }).catch(() => {
      setValidPassword(false);
    });
    console.log(validPassword);

    //setValidPassword(PWD_REGEX.test(password))
    //setValidMatch(password === matchPassword);
  }, [password, matchPassword])

  useEffect(() => {
    schema.validate({ password }).then(() => {
      setValidMatch(true);
    }).catch(() => {
      setValidMatch(false);
    });
    console.log(validMatch);
  }, [matchPassword])

  useEffect(() => {
    setErrMsg('');
  }, [username, password, matchPassword])

  const schema = yup.object().shape({
    username: yup
      .string()
      .min(3, "Username must be at least 3 characters")
      .max(23, "Username must be at most 23 characters")
      .required("Username is required!"),
    password: yup
      .string()
      .min(5, "Password must be at least 5 characters")
      .max(24, "Password must be at most 24 characters")
      .required("Password is required!")
      .matches(
        /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%])$/,
        'Password must contain at least one capital letter, one number, and one of !@#$%'
      ),
    matchPassword: yup
      .string()
      .oneOf([yup.ref("password")], "Passwords must match")
      .required('Confirm password is required'),
  });

  function handleSubmit(e) {
    e.preventDefault();

    console.log("Subbmited");
  }



  return (
    <div className='container'>
      {errMsg && errMsg.map((error) => (
        <p ref={errRef} className="errmsg" aria-live="assertive">{error}</p>
      ))}
      <h1>Register</h1>
      <form>
        <label htmlFor='username'>
          Username:
          <FontAwesomeIcon icon={faCheck} className={validUsername ? "valid" : "hide"} />
          <FontAwesomeIcon icon={faTimes} className={validUsername || !username ? "hide" : "invalid"} />
        </label>
        <input
          id='username'
          ref={usernameRef}
          type='text'
          name='username'
          autoComplete="off"
          onChange={(e) => setUsername(e.target.value)}
          onFocus={() => setUserFocus(true)}
          onBlur={() => setUserFocus(false)}
        />
        <p className={userFocus && username && !validUsername ? "instructions" : "hide"}>
          <FontAwesomeIcon icon={faInfoCircle} />
          4 to 24 characters.<br />
          Must begin with a letter.<br />
          Letters, numbers, underscores, hyphens allowed.
        </p>

        <label htmlFor='password'>
          Password:
          <FontAwesomeIcon icon={faCheck} className={validPassword ? "valid" : "hide"} />
          <FontAwesomeIcon icon={faTimes} className={validPassword || !password ? "hide" : "invalid"} />
        </label>
        <input
          id='password'
          type='password'
          name='password'

          onChange={(e) => setPassword(e.target.value)}
          onFocus={() => setPswFocus(true)}
          onBlur={() => setPswFocus(false)}
        />
        <p className={pswFocus && password && !validPassword ? "instructions" : "hide"}>
          <FontAwesomeIcon icon={faInfoCircle} />
          5 to 24 characters.<br />
          Must include uppercase and lowercase letters, a number and a special character.<br />
          Allowed special characters: !@#$%
        </p>

        <label htmlFor='matchPassword'>
          Confirm Password:
          <FontAwesomeIcon icon={faCheck} className={validMatch ? "valid" : "hide"} />
          <FontAwesomeIcon icon={faTimes} className={validMatch || !matchPassword ? "hide" : "invalid"} />
        </label>
        <input
          id='matchPassword'
          type='password'
          name='matchPassword'

          onChange={(e) => setMatchPassword(e.target.value)}
          onFocus={() => setMatchPswFocus(true)}
          onBlur={() => setMatchPswFocus(false)}
        />
        <p className={matchPswFocus && matchPassword && !validMatch ? "instructions" : "hide"}>
          <FontAwesomeIcon icon={faInfoCircle} />
          Must match the first password input field.
        </p>

        <button disabled={!validUsername || !validPassword || !validMatch ? true : false} onClick={handleSubmit}>Sign Up</button>
        <p>Already registered?</p>
        <Link to="/login" className='link'>Sign in</Link>
      </form>
    </div>
  )
}

export default Register
