import React, { useState } from 'react'
import * as yup from 'yup'
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import { faInfoCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Register2() {

  const schema = yup.object().shape({
    username: yup.string().required().min(3).max(23).message(`Invalid username. {\n} Follow instructions!`),
    password: yup.string().required().min(5).max(24).message(`Invalid password. {\n} Follow instructions!`),
    matchPassword: yup.oneOf([yup.ref("password")]).message("Passwords must match!")
  });

  const { register, handleSubmit, formState: { errors },
 } = useForm();
  const [userFocus, setUserFocus] = useState(false);
  const [pswFocus, setPswFocus] = useState(false);
  const [matchPswFocus, setMatchPswFocus] = useState(false);

  function onSubmit(data) {
    console.log(data);
  }

  return (
    <div className='container'>
      <h1>Register</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        <label htmlFor='username'>Username</label>
        <input
          type='text'
          id='username'
          name='username'
          {...register("username")}
          onFocus={() => setUserFocus(true)}
          onBlur={() => setUserFocus(false)}
        />
        <p className={userFocus ? "instructions" : "hide"}>
          <FontAwesomeIcon icon={faInfoCircle} />
          4 to 24 characters.<br />
          Must begin with a letter.<br />
          Letters, numbers, underscores, hyphens allowed.
        </p>

        <label htmlFor='password'>Password</label>
        <input
          type='password'
          id='password'
          name='password'
          {...register("password")}
          onFocus={() => setPswFocus(true)}
          onBlur={() => setPswFocus(false)}
        />
        <p className={pswFocus ? "instructions" : "hide"}>
          <FontAwesomeIcon icon={faInfoCircle} />
          5 to 24 characters.<br />
          Must include uppercase and lowercase letters, a number and a special character.<br />
          Allowed special characters: !@#$%
        </p>

        <label htmlFor='matchPassword'>Match Password</label>
        <input
          type='password'
          id='matchPassword'
          name='matchPassword'
          {...register("matchPassword")}
          onFocus={() => setMatchPswFocus(true)}
          onBlur={() => setMatchPswFocus(false)}
        />
        <p className={matchPswFocus ? "instructions" : "hide"}>
          <FontAwesomeIcon icon={faInfoCircle} />
          Must match the first password input field.
        </p>

        <button type='submit'>Submit</button>
      </form>
    </div>
  )
}

export default Register2
