import React, { useState } from 'react'
import * as yup from 'yup'
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import { faInfoCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Register2() {

  const { register, handleSubmit, formState: { errors } } = useForm();
  const [userFocus, setUserFocus] = useState(false);
  const [pswFocus, setPswFocus] = useState(false);
  const [matchPswFocus, setMatchPswFocus] = useState(false);

  function onSubmit(data) {
    console.log(data);
  }

  return (
    <div className='container'>
      <h1>Register</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        <label htmlFor='username'>Username</label>
        <input
          type='text'
          id='username'
          {...register("username")} 
          onFocus={() => setUserFocus(true)}
          onBlur={() => setUserFocus(false)}
        />
        <p className={userFocus && username && !validUsername ? "instructions" : "hide"}>
          <FontAwesomeIcon icon={faInfoCircle} />
          4 to 24 characters.<br />
          Must begin with a letter.<br />
          Letters, numbers, underscores, hyphens allowed.
        </p>

        <label htmlFor='password'>Password</label>
        <input
          type='password'
          id='password'
          {...register("password")} />

        <label htmlFor='matchPassword'>Match Password</label>
        <input
          type='password'
          id='matchPassword'
          {...register("matchPassword")} />

        <button type='submit'>Submit</button>
      </form>
    </div>
  )
}

export default Register2
