import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import * as yup from 'yup';

function Register() {
  const [inputs, setInputs] = useState({
    username: "",
    password: "",
    matchPassword: "",
  });
  const [validUsername, setValidUsername] = useState(false);
  const [validPassword, setValidPassword] = useState(false);

  const schema = yup.object().shape({
    username: yup
      .string()
      .min(3, "Username must be at least 3 characters")
      .max(23, "Username must be at most 23 characters")
      .required("Username is required!"),
    password: yup
      .string()
      .min(5, "Password must be at least 5 characters")
      .max(24, "Password must be at most 24 characters")
      .required("Password is required!")
      .matches(
        /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%])$/,
        'Password must contain at least one capital letter, one number, and one of !@#$%'
      ),
    matchPassword: yup
      .string()
      .oneOf([yup.ref("password")], "Passwords must match")
      .required('Confirm password is required'),
  });


  function handleInputChange(e) {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  }

  function handleSubmit(e) {
    e.preventDefault();

    schema.validate(inputs).then(() => {
      setValidUsername(true);
      setValidPassword(true);
    }).catch(() => {
      
    })
    console.log("", inputs);
  }

  useEffect(() => {

  }, [inputs])

  return (
    <div className='container'>
      <p ref={errRef} className={errMsg ? "errmsg" : ""} aria-live="assertive">{errMsg}</p>
      <h1>Register</h1>
      <form>
        <label htmlFor='username'>Username: </label>
        <input
          id='username'
          type='text'
          name='username'
          autoComplete="off"
          onChange={handleInputChange}
        />
        <label htmlFor='password'>Password: </label>
        <input
          id='password'
          type='password'
          name='password'
          onChange={handleInputChange}
        />
        <label htmlFor='matchPassword'>Confirm Password: </label>
        <input
          id='matchPassword'
          type='password'
          name='matchPassword'

          onChange={handleInputChange}
        />
        <button onClick={handleSubmit}>Sign Up</button>
        <p>Already registered?</p>
        <Link to="/login" className='link'>Sign in</Link>
      </form>
    </div>
  )
}

export default Register
