import axios from "axios"
import useAuth from "./useAuth";

const useRefreshToken = () => {
  const { setAuth } = useAuth();
  const refresh = async () => {
    try {
      const response = await axios.get("http://localhost:5000/refresh", {
        withCredentials: true
      });
      const roles = response.data.roles.map(role => role.code);
      setAuth((prev) => {
        return {
          ...prev,
          roles: response.data?.roles,
          accessToken: response.data?.newAccessToken
        }
      });
      
      console.log(roles);
      return { roles: roles, accessToken: response.data.newAccessToken };
    } catch (error) {
      console.error("Error at useresfreshhook, " + error);
    }
  }
  return refresh;
}

export default useRefreshToken;
