import React, { useEffect, useState, useRef } from 'react';
import { Link } from "react-router-dom";
import * as yup from 'yup';
import { faCheck, faTimes, faInfoCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Register() {
  const userRef = useRef();
  const errRef = useRef();

  const [inputs, setInputs] = useState({
    username: "",
    password: "",
    matchPassword: "",
  });

  const [validUsername, setValidUsername] = useState(false);
  const [validPassword, setValidPassword] = useState(false);
  const [validMatch, setValidMatch] = useState(false);

  const [errMsg, setErrMsg] = useState([]);

  const [userFocus, setUserFocus] = useState(false);
  const [pswFocus, setPswFocus] = useState(false);
  const [matchPswFocus, setMatchPswFocus] = useState(false);

  useEffect(() => {
    userRef.current.focus();
  }, []);

  

  const schema = yup.object().shape({
    username: yup
      .string()
      .min(3, "Username must be at least 3 characters")
      .max(23, "Username must be at most 23 characters")
      .required("Username is required!"),
    password: yup
      .string()
      .min(5, "Password must be at least 5 characters")
      .max(24, "Password must be at most 24 characters")
      .required("Password is required!")
      .matches(
        /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%])$/,
        'Password must contain at least one capital letter, one number, and one of !@#$%'
      ),
    matchPassword: yup
      .string()
      .oneOf([yup.ref("password")], "Passwords must match")
      .required('Confirm password is required'),
  });

  function handleSubmit(e) {
    e.preventDefault();

    schema.validate(inputs).then(() => {
      setValidUsername(true);
      setValidPassword(true);
      setValidMatch(true);
    }).catch((validationErrors) => {
      setErrMsg(validationErrors.errors);
    })
    console.log("", inputs);
  }



  return (
    <div className='container'>
      {errMsg && errMsg.map((error) => (
        <p ref={errRef} className="errmsg" aria-live="assertive">{error}</p>
      ))}
      <h1>Register</h1>
      <form>
        <label htmlFor='username'>
          Username:
          <FontAwesomeIcon icon={faCheck} className={validUsername ? "valid" : "hide"} />
          <FontAwesomeIcon icon={faTimes} className={validUsername || !inputs.username ? "hide" : "invalid"} />
        </label>
        <input
          id='username'
          ref={userRef}
          type='text'
          name='username'
          autoComplete="off"
          onChange={handleInputChange}
          onFocus={() => setUserFocus(true)}
          onBlur={() => setUserFocus(true)}
        />
        <p className={userFocus && inputs.username && !validUsername ? "instructions" : "hide"}>
          <FontAwesomeIcon icon={faInfoCircle} />
          4 to 24 characters.<br />
          Must begin with a letter.<br />
          Letters, numbers, underscores, hyphens allowed.
        </p>

        <label htmlFor='password'>
          Password:
          <FontAwesomeIcon icon={faCheck} className={validPassword ? "valid" : "hide"} />
          <FontAwesomeIcon icon={faTimes} className={validPassword || !inputs.password ? "hide" : "invalid"} />
        </label>
        <input
          id='password'
          type='password'
          name='password'
          onChange={handleInputChange}
          onFocus={() => setPswFocus(true)}
          onBlur={() => setPswFocus(true)}
        />
        <p className={pswFocus && inputs.password && !validPassword ? "instructions" : "hide"}>
          <FontAwesomeIcon icon={faInfoCircle} />
          5 to 24 characters.<br />
          Must include uppercase and lowercase letters, a number and a special character.<br />
          Allowed special characters: !@#$%
        </p>

        <label htmlFor='matchPassword'>
          Confirm Password:
          <FontAwesomeIcon icon={faCheck} className={validMatch ? "valid" : "hide"} />
          <FontAwesomeIcon icon={faTimes} className={validMatch || !inputs.matchPassword ? "hide" : "invalid"} />
        </label>
        <input
          id='matchPassword'
          type='password'
          name='matchPassword'
          onChange={handleInputChange}
          onFocus={() => setMatchPswFocus(true)}
          onBlur={() => setMatchPswFocus(true)}
        />
        <p className={matchPswFocus && inputs.matchPassword && !validMatch ? "instructions" : "hide"}>
          <FontAwesomeIcon icon={faInfoCircle} />
          Must match the first password input field.
        </p>

        <button disabled={!validUsername || !validPassword || !validMatch ? true : false} onClick={handleSubmit}>Sign Up</button>
        <p>Already registered?</p>
        <Link to="/login" className='link'>Sign in</Link>
      </form>
    </div>
  )
}

export default Register
